using Microsoft.EntityFrameworkCore;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;namespace BusinessLayer.Repository.IRepository{    public class PagedList<T> : List<T>    {        public PagedList(List<T> currentPage, int count, int pageNumber)        {            CurrentPage = pageNumber;            TotalPages = (int)Math.Ceiling(count / (double)5);            PageSize = 5;            TotalCount = count;            AddRange(currentPage);        }        public int CurrentPage { get; set; }        public int TotalPages { get; set; }        public int PageSize { get; set; }        public int TotalCount { get; set; }        public static PagedList<T> Create(List<T> source, int pageNumber)        {            var count = source.Count();            var items = source.Skip((pageNumber) * 5).Take(5).ToList();            return new PagedList<T>(items, count, pageNumber);        }    }}